// CommonJS export

function print(log) {
  if (log === undefined) {
    console.log()
    return 1;
  }

  console.log(log);
}

print.err = function (log) {
   if (log === undefined) {
     console.log();
     return 1;
   }

  console.error("\x1b[31m%s\x1b[0m", log);
};

print.wrn = function (log) {
  if (log === undefined) {
    console.log();
    return 1;
  }
  console.warn("\x1b[33m%s\x1b[0m", log);
};

print.info = function (log) {
   if (log === undefined) {
     console.log();
     return 1;
   }
  console.info("\x1b[36m%s\x1b[0m", log);
};

// Export for Node.js
if (typeof module !== "undefined" && module.exports) {
  module.exports = print;
} else {
  // Export for browser
  window.print = print;
}
